import React, { Component } from 'react';
import cSharp from './Images/cSharpLogo.png';
import unity from './Images/unity.png';
import reactlogo from './Images/reactlogo.png';
import javascriptlogo from './Images/javascriptlogo.png';
let battlethronesLogo= 'images/portfolio/battlethroneslogo.png'

let projects = [
  {
    "name":"RZ Drift",
    "description":"RZ Drift takes drifting to a whole new level. Realistic physics in fantastical environments create an unreal driving experience",
    "logo":"images/portfolio/rz_drift_logo.jpeg",
    'gif':'images/portfolio/rz_drift_gif.gif',
    "link": "https://apps.apple.com/us/app/rz-drift/id1467063778",
    "tech": ["C#", "Unity"]
  },
  {
    "name":"Battlethrones",
    "description":"RZ Drift takes drifting to a whole new level. Realistic physics in fantastical environments create an unreal driving experience",
    "logo":"images/portfolio/battlethroneslogo.jpeg",
    'gif':'images/portfolio/rz_drift_gif.gif',
    "link": "https://apps.apple.com/us/app/rz-drift/id1467063778",
    "tech": ["Javascript", "React"]
  },
]

let logos = [
  {
    "name": "Javascript",
    "url": './Images/javascriptlogo.png'
  },
  {
    "name": "C#",
    "url": './Images/cSharpLogo.png'
  },
  {
    "name": "Unity",
    "url": './Images/unityLogo.png'
  },
  {
    "name": "React",
    "url": './Images/reactlogo.png'
  }
]
// {
//   "name":"BattleThrones",
//   "description":"Game of Thrones Themed Battleship",
//   "imgurl":"images/portfolio/battlethrones.jpeg",
//   "link": ""
// },
const screenWidth = {
  "--screenWidth": "1000"
};
const screenHeight = {
  "--screenHeight": "1000"
};
let rzDriftGif = "images/portfolio/rz_drift_gif.gif"
let battlethronesGif = "images/portfolio/battlethronesgif.gif"
export default class Porfolio extends Component {
  constructor(props) {
    super(props);
    this.state = { width: 0, height: 0 };
  }

  componentDidMount() {
    this.updateWindowDimensions();
    window.addEventListener('resize', this.updateWindowDimensions);
  }

  componentWillUnmount() {
    window.removeEventListener('resize', this.updateWindowDimensions);
  }

  updateWindowDimensions = () => {
    Object.keys(screenWidth).map(key => {
      screenWidth[key] = window.innerWidth
      const value = screenWidth[key];
      document.documentElement.style.setProperty(key, value);
    });
    Object.keys(screenHeight).map(key => {
      screenHeight[key] = window.innerHeight
      const value = screenHeight[key];
      document.documentElement.style.setProperty(key, value);
    });
    this.setState({ width: window.innerWidth, height: window.innerHeight });
  }

  getLogoUrl = (name) => {
    for(let i = 0; i < logos.length; i++){
      if(name === logos[i].name){
        return logos[i].url
      }
    }
  }

  render() {
    let projectGrid = projects.map(project => {
      return(
        <div className = "grid2">
          <a className = "gridSquareLeft" href={project.link} target="_blank">
              <img className = "gridLogo" src={project.logo}/>
              <div className="principles-p">
                {project.tech.map((logo, index) => {
                  return(
                    <div>
                      <div className="principle-item-p">
                        <h3 className = "flexTitle">{logo.name}</h3>
                        <img src={logo.url} alt="" />
                      </div>
                      {index === project.tech.length -1 ? null:  <div className="principle-item-p" />}
                    </div>
                  )
                })}
              </div>
          </a>
          <a className = "gridSquare"></a>
          <a className = "gridSquareRight" href={project.link} target="_blank">
            <img className = "gridGif" src={project.gif}/>
            <p className = "gridTitle">{project.description}</p>
          </a>
        </div>
      )
    })
    let rzDriftGrid = []
    rzDriftGrid.push(
    <a className = "gridSquareLeft" href={"https://apps.apple.com/us/app/rz-drift/id1467063778"} target="_blank">
        <img className = "gridLogo" src={"images/portfolio/rz_drift_logo.jpeg"}/>
        <div className="principles-p">
          <div className="principle-item-p">
            <h3 className = "flexTitle">C#</h3>
            <img src={cSharp} alt="" />
          </div>

          <div className="principle-item-p" />

          <div className="principle-item-p">
            <h3 className = "flexTitle">Unity</h3>
            <img src={unity} alt="" />
          </div>
        </div>
    </a>)
    rzDriftGrid.push(<a className = "gridSquare"></a>)
    rzDriftGrid.push(
    <a className = "gridSquareRight" href={"https://apps.apple.com/us/app/rz-drift/id1467063778"} target="_blank">
      <img className = "gridGif" src={rzDriftGif}/>
      <p className = "gridTitle">RZ Drift takes drifting to a whole new level. Realistic physics in fantastical environments create an unreal driving experience.</p>
    </a>)

    let battleThronesGrid = []
    battleThronesGrid.push(
    <a className = "gridSquareLeft" href={"https://apps.apple.com/us/app/rz-drift/id1467063778"} target="_blank">
        <img className = "gridLogo" src={battlethronesLogo}/>
        <div className="principles-p">
          <div className="principle-item-p">
            <h3 className = "flexTitle">Javascript</h3>
            <img src={javascriptlogo} alt="" />
          </div>

          <div className="principle-item-p" />

          <div className="principle-item-p">
            <h3 className = "flexTitle">React</h3>
            <img src={reactlogo} alt="" />
          </div>
        </div>
    </a>)
    battleThronesGrid.push(<a className = "gridSquare"></a>)
    battleThronesGrid.push(
    <a className = "gridSquareRight" href={"https://apps.apple.com/us/app/rz-drift/id1467063778"} target="_blank">
      <img className = "gridGif" src={battlethronesGif}/>
      <p className = "gridTitle">Fight for the fate of the Seven Kingdoms in this Game of Thrones theme Battleship game.</p>
    </a>)
    return (
      <section id="portfolio">
        {projectGrid}
        <div className = "grid2">
          {rzDriftGrid}
        </div>
        <div className = "grid2">
          {battleThronesGrid}
        </div>
      </section>
        );
  }
}
